import React from 'react';
import DataSet from './DataSet';

const App = () => {
  const users = [
    { id: 1, name: 'John', age: 25, email: 'john@example.com' },
    { id: 2, name: 'Jane', age: 30, email: 'jane@example.com' },
    { id: 3, name: 'Bob', age: 35, email: 'bob@example.com' },
  ];

  const headers = [
    { property: 'name', title: 'Full Name' },
    { property: 'age', title: 'Age' },
    { property: 'email', title: 'Email Address' },
  ];

  // Кастомный рендер для ячейки
  const renderCell = (item, property, index) => (
    <td key={index} style={{ 
      color: property === 'age' && item[property] > 30 ? 'red' : 'inherit',
      fontWeight: property === 'name' ? 'bold' : 'normal'
    }}>
      {item[property]}
    </td>
  );

  // Кастомный рендер для заголовка
  const renderHeader = (header, index) => (
    <th key={index} style={{ 
      backgroundColor: '#333',
      color: 'white',
      padding: '10px'
    }}>
      {header.title}
    </th>
  );

  return (
    <div>
      <h2>Default DataSet (headers inferred from data)</h2>
      <DataSet data={users} />

      <h2>DataSet with custom headers</h2>
      <DataSet 
        headers={headers} 
        data={users} 
      />

      <h2>DataSet with custom rendering</h2>
      <DataSet 
        headers={headers} 
        data={users} 
        renderHeader={renderHeader}
        renderCell={renderCell}
      />
    </div>
  );
};

export default App;
