import React, { useState } from 'react';

const DataSet = ({ 
  headers = [], 
  data = [], 
  renderHeader = (header, index) => <th key={index}>{header.title || header}</th>,
  renderCell = (item, property, index) => <td key={index}>{item[property]}</td>
}) => {
  const [selectedRows, setSelectedRows] = useState(new Set());

  const effectiveHeaders = headers.length > 0 
    ? headers 
    : data.length > 0 
      ? Object.keys(data[0]).map(key => ({ property: key, title: key }))
      : [];

  const handleRowSelect = (index, ctrlKey) => {
    setSelectedRows(prev => {
      const newSelection = new Set(prev);

      if (ctrlKey) {
        if (newSelection.has(index)) {
          newSelection.delete(index);
        } else {
          newSelection.add(index);
        }
      } else {
        if (newSelection.has(index) && newSelection.size === 1) {
          newSelection.clear();
        } else {
          newSelection.clear();
          newSelection.add(index);
        }
      }

      return newSelection;
    });
  };

  const styles = {
    table: {
      borderCollapse: 'collapse',
      width: '100%',
      margin: '20px 0',
    },
    th: {
      border: '1px solid #ddd',
      padding: '8px',
      textAlign: 'left',
      backgroundColor: '#f2f2f2',
    },
    td: {
      border: '1px solid #ddd',
      padding: '8px',
    },
    rowSelector: {
      width: '20px',
      cursor: 'pointer',
      backgroundColor: '#f9f9f9',
    },
    selectedRow: {
      backgroundColor: '#e6f7ff',
    },
  };

  return (
    <table style={styles.table}>
      <thead>
        <tr>
          <th style={styles.rowSelector}></th>
          {effectiveHeaders.map((header, index) => 
            renderHeader(header, index)
          )}
        </tr>
      </thead>
      <tbody>
        {data.map((item, rowIndex) => (
          <tr 
            key={rowIndex} 
            style={selectedRows.has(rowIndex) ? styles.selectedRow : {}}
          >
            <td 
              style={styles.rowSelector}
              onClick={(e) => handleRowSelect(rowIndex, e.ctrlKey)}
            >
              {selectedRows.has(rowIndex) ? '✓' : ''}
            </td>
            {effectiveHeaders.map((header, cellIndex) => {
              const property = typeof header === 'object' ? header.property : header;
              return renderCell(item, property, cellIndex);
            })}
          </tr>
        ))}
      </tbody>
    </table>
  );
};

export default DataSet;
