// DataSet.js
import React, { useState } from 'react';
import PropTypes from 'prop-types';

const DataSet = ({ data, onAdd, onDelete, onUpdate }) => {
  const [selectedCommentIds, setSelectedCommentIds] = useState([]);
  const [newCommentData, setNewCommentData] = useState({
    postId: 1,
    name: '',
    email: '',
    body: ''
  });
  const [editingCommentId, setEditingCommentId] = useState(null);
  const [editFormData, setEditFormData] = useState({});

  const handleAddNewComment = () => {
    if (!newCommentData.name || !newCommentData.body) {
      alert('Please enter both name and comment body');
      return;
    }
    onAdd(newCommentData);
    setNewCommentData({
      postId: 1,
      name: '',
      email: '',
      body: ''
    });
  };

  const handleDeleteSelected = () => {
    if (selectedCommentIds.length > 0) {
      if (window.confirm(`Delete ${selectedCommentIds.length} selected comments?`)) {
        onDelete(selectedCommentIds);
        setSelectedCommentIds([]);
      }
    }
  };

  const startEditingComment = (comment) => {
    setEditingCommentId(comment.id);
    setEditFormData({
      name: comment.name,
      email: comment.email,
      body: comment.body
    });
  };

  const saveEditedComment = () => {
    if (editingCommentId) {
      onUpdate(editingCommentId, editFormData);
      setEditingCommentId(null);
    }
  };

  const tableHeaders = ['Select', 'ID', 'Post ID', 'Name', 'Email', 'Body', 'Actions'];

  return (
    <div style={{ padding: '20px' }}>
      <div style={{ marginBottom: '20px', padding: '10px', border: '1px solid #ddd', borderRadius: '5px' }}>
        <h3>Add New Comment</h3>
        <div style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
          <input
            type="number"
            value={newCommentData.postId}
            onChange={(e) => setNewCommentData({...newCommentData, postId: +e.target.value})}
            placeholder="Post ID"
            style={{ padding: '8px' }}
          />
          <input
            value={newCommentData.name}
            onChange={(e) => setNewCommentData({...newCommentData, name: e.target.value})}
            placeholder="Name"
            style={{ padding: '8px' }}
            required
          />
          <input
            value={newCommentData.email}
            onChange={(e) => setNewCommentData({...newCommentData, email: e.target.value})}
            placeholder="Email"
            style={{ padding: '8px' }}
          />
          <textarea
            value={newCommentData.body}
            onChange={(e) => setNewCommentData({...newCommentData, body: e.target.value})}
            placeholder="Comment body"
            style={{ padding: '8px', minHeight: '60px' }}
            required
          />
          <button 
            onClick={handleAddNewComment}
            style={{ padding: '8px', backgroundColor: '#4CAF50', color: 'white', border: 'none' }}
          >
            Add Comment
          </button>
        </div>
      </div>

      <div style={{ marginBottom: '10px' }}>
        <button 
          onClick={handleDeleteSelected}
          disabled={selectedCommentIds.length === 0}
          style={{ 
            padding: '8px',
            backgroundColor: selectedCommentIds.length ? '#f44336' : '#cccccc',
            color: 'white',
            border: 'none',
            cursor: selectedCommentIds.length ? 'pointer' : 'not-allowed'
          }}
        >
          Delete Selected ({selectedCommentIds.length})
        </button>
      </div>

      <table style={{
        borderCollapse: 'collapse',
        width: '100%',
        border: '1px solid #ddd',
        fontFamily: 'Arial, sans-serif'
      }}>
        <thead>
          <tr>
            {tableHeaders.map((header, index) => (
              <th key={index} style={{
                border: '1px solid #ddd',
                padding: '12px',
                backgroundColor: '#f2f2f2',
                textAlign: 'left'
              }}>
                {header}
              </th>
            ))}
          </tr>
        </thead>
        <tbody>
          {data.map(comment => (
            <tr key={comment.id} style={{
              backgroundColor: selectedCommentIds.includes(comment.id) ? '#b3e0ff' : 'transparent'
            }}>
              <td style={{ border: '1px solid #ddd', padding: '8px' }}>
                <input
                  type="checkbox"
                  checked={selectedCommentIds.includes(comment.id)}
                  onChange={(e) => {
                    if (e.target.checked) {
                      setSelectedCommentIds([...selectedCommentIds, comment.id]);
                    } else {
                      setSelectedCommentIds(selectedCommentIds.filter(id => id !== comment.id));
                    }
                  }}
                />
              </td>
              <td style={{ border: '1px solid #ddd', padding: '8px' }}>{comment.id}</td>
              <td style={{ border: '1px solid #ddd', padding: '8px' }}>{comment.postId}</td>
              <td style={{ border: '1px solid #ddd', padding: '8px' }}>
                {editingCommentId === comment.id ? (
                  <input
                    value={editFormData.name}
                    onChange={(e) => setEditFormData({...editFormData, name: e.target.value})}
                    style={{ width: '100%', padding: '4px' }}
                  />
                ) : (
                  comment.name
                )}
              </td>
              <td style={{ border: '1px solid #ddd', padding: '8px' }}>
                {editingCommentId === comment.id ? (
                  <input
                    value={editFormData.email}
                    onChange={(e) => setEditFormData({...editFormData, email: e.target.value})}
                    style={{ width: '100%', padding: '4px' }}
                  />
                ) : (
                  comment.email
                )}
              </td>
              <td style={{ border: '1px solid #ddd', padding: '8px' }}>
                {editingCommentId === comment.id ? (
                  <textarea
                    value={editFormData.body}
                    onChange={(e) => setEditFormData({...editFormData, body: e.target.value})}
                    style={{ width: '100%', padding: '4px', minHeight: '50px' }}
                  />
                ) : (
                  comment.body
                )}
              </td>
              <td style={{ border: '1px solid #ddd', padding: '8px' }}>
                {editingCommentId === comment.id ? (
                  <>
                    <button 
                      onClick={saveEditedComment}
                      style={{ marginRight: '5px', padding: '4px 8px', backgroundColor: '#4CAF50', color: 'white' }}
                    >
                      Save
                    </button>
                    <button 
                      onClick={() => setEditingCommentId(null)}
                      style={{ padding: '4px 8px', backgroundColor: '#f44336', color: 'white' }}
                    >
                      Cancel
                    </button>
                  </>
                ) : (
                  <button 
                    onClick={() => startEditingComment(comment)}
                    style={{ padding: '4px 8px', backgroundColor: '#2196F3', color: 'white' }}
                  >
                    Edit
                  </button>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

DataSet.propTypes = {
  data: PropTypes.array.isRequired,
  onAdd: PropTypes.func.isRequired,
  onDelete: PropTypes.func.isRequired,
  onUpdate: PropTypes.func.isRequired,
};

export default DataSet;
