// DataSet.js
import React, { useState } from 'react';
import PropTypes from 'prop-types';

const DataSet = ({ headers, data, renderHeader, renderCell }) => {
  const [selectedRows, setSelectedRows] = useState(new Set());

  const handleRowSelection = (rowIndex, event) => {
    // Проверяем, что клик был по левой ячейке (первой колонке)
    if (event.target.cellIndex !== 0) return;

    if (event.ctrlKey || event.metaKey) {
      // При зажатом Ctrl добавляем/удаляем строку из выделения
      const newSelection = new Set(selectedRows);
      if (newSelection.has(rowIndex)) {
        newSelection.delete(rowIndex);
      } else {
        newSelection.add(rowIndex);
      }
      setSelectedRows(newSelection);
    } else {
      // Без Ctrl выделяем только эту строку (или снимаем выделение, если уже выделена)
      if (selectedRows.size === 1 && selectedRows.has(rowIndex)) {
        setSelectedRows(new Set());
      } else {
        setSelectedRows(new Set([rowIndex]));
      }
    }
  };

  const handleCellClick = (rowIndex, colIndex, event) => {
    // Обычные ячейки не выделяем при клике (только через левую колонку)
    if (colIndex !== 0) return;
  };

  // Если заголовки не переданы, берем ключи из первого элемента данных
  const effectiveHeaders = headers || (data.length > 0 ? Object.keys(data[0]) : []);

  return (
    <table style={{
      borderCollapse: 'collapse',
      width: '100%',
      border: '1px solid #ddd',
      fontFamily: 'Arial, sans-serif'
    }}>
      <thead>
        <tr>
          <th style={{
            border: '1px solid #ddd',
            padding: '8px',
            backgroundColor: '#f2f2f2',
            width: '30px',
            textAlign: 'center'
          }}></th>
          {effectiveHeaders.map((header, idx) => (
            <th key={idx} style={{
              border: '1px solid #ddd',
              padding: '8px',
              backgroundColor: '#f2f2f2',
              textAlign: 'left'
            }}>
              {renderHeader ? renderHeader(header, idx) : header}
            </th>
          ))}
        </tr>
      </thead>
      <tbody>
        {data.map((item, rowIndex) => (
          <tr key={rowIndex}>
            <td
              style={{
                border: '1px solid #ddd',
                padding: '8px',
                cursor: 'pointer',
                backgroundColor: selectedRows.has(rowIndex) ? '#e6f7ff' : '#f9f9f9'
              }}
              onClick={(e) => handleRowSelection(rowIndex, e)}
            >
              {selectedRows.has(rowIndex) ? '✓' : ''}
            </td>
            {effectiveHeaders.map((header, colIndex) => (
              <td 
                key={colIndex}
                style={{
                  border: '1px solid #ddd',
                  padding: '8px',
                  backgroundColor: selectedRows.has(rowIndex) ? '#e6f7ff' : 'transparent'
                }}
                onClick={(e) => handleCellClick(rowIndex, colIndex, e)}
              >
                {renderCell ? renderCell(item, header, rowIndex, colIndex) : item[header]}
              </td>
            ))}
          </tr>
        ))}
      </tbody>
    </table>
  );
};

DataSet.propTypes = {
  headers: PropTypes.array,
  data: PropTypes.array.isRequired,
  renderHeader: PropTypes.func,
  renderCell: PropTypes.func,
};

export default DataSet;
