// В компоненте DataApp, обновите часть с Formik:
<Formik
  initialValues={initialValues}
  validationSchema={validationSchema}
  onSubmit={(values, { resetForm }) => {
    handleAdd(values);
    resetForm();
  }}
>
  {({ errors, touched }) => (
    <Form style={formStyle}>
      {fields.map(field => (
        <div 
          key={field.name} 
          style={{
            ...fieldStyle,
            gridColumn: field.as === 'textarea' ? '1 / -1' : 'auto'
          }}
        >
          <label htmlFor={field.name} style={labelStyle}>
            {field.label}
          </label>
          <Field 
            type={field.type || 'text'} 
            name={field.name} 
            id={field.name}
            as={field.as || 'input'}
            style={field.as === 'textarea' ? textareaStyle : inputStyle}
          />
          <ErrorMessage name={field.name} component="div" style={errorStyle} />
        </div>
      ))}
      <div style={{ gridColumn: '1 / -1' }}>
        <button 
          type="submit" 
          style={{
            padding: '10px 20px',
            backgroundColor: '#4CAF50',
            color: 'white',
            border: 'none',
            borderRadius: '4px',
            cursor: 'pointer',
            fontSize: '16px',
            marginTop: '10px'
          }}
        >
          Add Item
        </button>
      </div>
    </Form>
  )}
</Formik>
