import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import Comments from './Comments';
import Posts from './Posts';
import Albums from './Albums';
import TODOS from './TODOS';
import Users from './Users';

function App() {
  return (
    <Router>
      <div style={{ display: 'flex', minHeight: '100vh' }}>
        <nav style={{
          width: '220px',
          backgroundColor: '#e1f5fe', 
          color: '#01579b', 
          padding: '0',
          boxShadow: '2px 0 5px rgba(0,0,0,0.05)',
          position: 'sticky',
          top: 0,
          height: '100vh',
          borderRight: '1px solidrgb(18, 118, 164)'
        }}>
          <div style={{
            padding: '20px',
            marginBottom: '10px',
            fontSize: '22px',
            fontWeight: 'bold',
            display: 'flex',
            alignItems: 'center',
            backgroundColor: '#8a7f8e', 
            color: 'white' 
          }}>
            <span>VOU BLYA</span>
          </div>

          <ul style={{ listStyle: 'none', padding: 0, margin: 0 }}>
            {[
              { to: '/posts', icon: 'ðŸ“', text: 'Posts' },
              { to: '/albums', icon: 'ðŸ“·', text: 'Albums' },
              { to: '/todos', icon: 'âœ…', text: 'Todos' },
              { to: '/users', icon: 'ðŸ‘¥', text: 'Users' },
              { to: '/comments', icon: 'ðŸ’¬', text: 'Comments' }
            ].map((item, index) => (
              <li key={index} style={{ margin: 0 }}>
                <Link 
                  to={item.to}
                  style={{
                    display: 'flex',
                    alignItems: 'center',
                    padding: '12px 20px',
                    color: '#4fc3f7',
                    textDecoration: 'none',
                    transition: 'all 0.2s ease',
                    fontSize: '15px',
                    borderLeft: '3px solid transparent',
                    ':hover': {
                      backgroundColor: '#c5d0e6',
                      borderLeft: '3px solidrgb(3, 111, 170)'
                    }
                  }}
                  activeStyle={{
                    backgroundColor: '#c5d0e6',
                    fontWeight: 'bold',
                    borderLeft: '3px solidrgb(19, 115, 166)'
                  }}
                >
                  <span style={{ marginRight: '12px', fontSize: '16px' }}>{item.icon}</span>
                  {item.text}
                </Link>
              </li>
            ))}
          </ul>

          <div style={{
            marginTop: 'auto',
            padding: '15px',
            fontSize: '11px',
            color: '#c5d0e6',
            textAlign: 'center',
            borderTop: '1px solidrgb(15, 104, 145)',
            backgroundColor: '#c5d0e6'
          }}>
            Â© 2025 made by Glebasik and Mishanya
          </div>
        </nav>

        <main style={{ 
          flex: 1, 
          padding: '25px',
          backgroundColor: '#f5faff'
        }}>
          <Routes>
            <Route path="/posts" element={<Posts />} />
            <Route path="/albums" element={<Albums />} />
            <Route path="/todos" element={<TODOS />} />
            <Route path="/users" element={<Users />} />
            <Route path="/comments" element={<Comments />} />
            <Route path="/" element={<Posts />} />
          </Routes>
        </main>
      </div>
    </Router>
  );
}

export default App;
