import { useState, useEffect } from 'react';
import DataSet from './DataSet';

export default function CommentsApp() {
  const [comments, setComments] = useState([]);
  const [optimisticComments, setOptimisticComments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    async function fetchComments() {
      try {
        const response = await fetch('https://jsonplaceholder.typicode.com/comments');

        if (!response.ok) {
          throw new Error('Failed to fetch comments');
        }

        const data = await response.json();
        setComments(data);
        setOptimisticComments(data);
      } catch (err) {
        if (err instanceof Error) {
          setError(err.message);
        } else {
          setError('Unknown error');
        }
      } finally {
        setLoading(false);
      }
    }

    fetchComments();
  }, []);

  const handleAdd = async (comment) => {
    const tempId = Date.now();
    const optimisticComment = { ...comment, id: tempId };

    setOptimisticComments(prev => [...prev, optimisticComment]);

    try {
      const response = await fetch('https://jsonplaceholder.typicode.com/comments', {
        method: 'POST',
        body: JSON.stringify(comment),
        headers: {
          'Content-type': 'application/json; charset=UTF-8',
        },
      });

      if (!response.ok) throw new Error('Failed to add comment');

      const serverComment = await response.json();

      // Используем tempId вместо 501
      setComments(prev => [...prev, { ...serverComment, id: tempId }]);
    } catch (err) {
      setOptimisticComments(prevComments => prevComments.filter(c => c.id !== tempId));

      if (err instanceof Error) {
        setError(err.message);
      } else {
        setError('Failed to add comment');
      }
    }
  };

  const handleDelete = async (ids) => {
    const backup = [...optimisticComments];

    setOptimisticComments(prevComments => prevComments.filter(c => !ids.includes(c.id)));

    try {
      const responses = await Promise.all(
        ids.map(id => 
          fetch(`https://jsonplaceholder.typicode.com/comments/${id}`, {
            method: 'DELETE',
          })
        )
      );

      const allOk = responses.every(r => r.ok);
      if (!allOk) {
        throw new Error('Failed to delete some comments');
      }

      setComments(prevComments => prevComments.filter(c => !ids.includes(c.id)));
    } catch (err) {
      setOptimisticComments(backup);

      if (err instanceof Error) {
        setError(err.message);
      } else {
        setError('Failed to delete comments');
      }
    }
  };

  const handleUpdate = async (id, updates) => {
    const original = optimisticComments.find(c => c.id === id);

    setOptimisticComments(prevComments => {
      return prevComments.map(c => {
        if (c.id === id) {
          return { ...c, ...updates };
        }
        return c;
      });
    });

    try {
      const response = await fetch(`https://jsonplaceholder.typicode.com/comments/${id}`, {
        method: 'PATCH',
        body: JSON.stringify(updates),
        headers: {
          'Content-type': 'application/json; charset=UTF-8',
        },
      });

      if (!response.ok) {
        throw new Error('Failed to update comment');
      }

      const updatedComment = await response.json();

      setComments(prevComments => {
        return prevComments.map(c => {
          if (c.id === id) {
            return updatedComment;
          }
          return c;
        });
      });
    } catch (err) {
      setOptimisticComments(prevComments => {
        return prevComments.map(c => {
          if (c.id === id) {
            return original;
          }
          return c;
        });
      });

      if (err instanceof Error) {
        setError(err.message);
      } else {
        setError('Failed to update comment');
      }
    }
  };

  if (loading) {
    return <div>Glebasik uffff...</div>;
  }

  if (error) {
    return <div>I'm stupid coder and my code ending with error: {error}</div>;
  }

  return (
    <div className="comments-app">
      <h1>Comments</h1>
      <DataSet 
        data={optimisticComments}
        onAdd={handleAdd}
        onDelete={handleDelete}
        onUpdate={handleUpdate}
      />
      {error && <div className="error">{error}</div>}
    </div>
  );
}
