const DataSet = ({ data, onAdd, onDelete, onUpdate }) => {
  const [selectedIds, setSelectedIds] = useState([]);
  const [newComment, setNewComment] = useState({
    postId: 1,
    name: '',
    email: '',
    body: ''
  });

  const handleAdd = () => {
    onAdd(newComment);
    setNewComment({
      postId: 1,
      name: '',
      email: '',
      body: ''
    });
  };

  const handleDeleteSelected = () => {
    if (selectedIds.length > 0) {
      onDelete(selectedIds);
      setSelectedIds([]);
    }
  };

  return (
    <div>
      <div className="controls">
        <h3>Add New Comment</h3>
        <input
          value={newComment.name}
          onChange={(e) => setNewComment({...newComment, name: e.target.value})}
          placeholder="Name"
        />
        <input
          value={newComment.email}
          onChange={(e) => setNewComment({...newComment, email: e.target.value})}
          placeholder="Email"
        />
        <textarea
          value={newComment.body}
          onChange={(e) => setNewComment({...newComment, body: e.target.value})}
          placeholder="Comment text"
        />
        <button onClick={handleAdd}>Add Comment</button>

        <button 
          onClick={handleDeleteSelected}
          disabled={selectedIds.length === 0}
        >
          Delete Selected ({selectedIds.length})
        </button>
      </div>

      <table>
        <thead>
          <tr>
            <th>Select</th>
            <th>ID</th>
            <th>Name</th>
            <th>Email</th>
            <th>Body</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {data.map(comment => (
            <tr key={comment.id}>
              <td>
                <input
                  type="checkbox"
                  checked={selectedIds.includes(comment.id)}
                  onChange={(e) => {
                    if (e.target.checked) {
                      setSelectedIds([...selectedIds, comment.id]);
                    } else {
                      setSelectedIds(selectedIds.filter(id => id !== comment.id));
                    }
                  }}
                />
              </td>
              <td>{comment.id}</td>
              <td>{comment.name}</td>
              <td>{comment.email}</td>
              <td>{comment.body}</td>
              <td>
                <button onClick={() => onUpdate(comment.id, { body: 'Updated text' })}>
                  Edit
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};
