import React, { useState } from "react";
const cn = (...classes) => classes.filter(Boolean).join(" ");

const DataSet = ({ columns, data, renderRow, renderHeader }) => {
  const [selectedRows, setSelectedRows] = useState(new Set());

  const handleRowClick = (id, event) => {
    setSelectedRows((prev) => {
  let newSelection = new Set(prev); // Меняем const на let
  if (event.ctrlKey) {
    newSelection.has(id) ? newSelection.delete(id) : newSelection.add(id);
  } else {
    newSelection = new Set(newSelection.has(id) ? [] : [id]);
  }
  return newSelection;
});
  };

  return (
    <table className="border-collapse w-full">
      <thead>
        <tr>
          <th className="border px-4 py-2"></th> {/* Selection column */}
          {columns.map((col, index) => (
            <th key={index} className="border px-4 py-2">{renderHeader(col)}</th>
          ))}
        </tr>
      </thead>
      <tbody>
        {data.map((row, index) => (
          <tr
            key={index}
            className={cn(
              "border cursor-pointer", 
              selectedRows.has(index) ? "bg-blue-200" : ""
            )}
            onClick={(e) => handleRowClick(index, e)}
          >
            <td className="border px-4 py-2 bg-gray-100"></td>
            {columns.map((col, colIndex) => (
              <td key={colIndex} className="border px-4 py-2">
                {renderRow(row, col)}
              </td>
            ))}
          </tr>
        ))}
      </tbody>
    </table>
  );
};

export default DataSet;
