import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import Comments from './Comments';
import Posts from './Posts';
import Albums from './Albums';
import TODOS from './TODOS';
import Users from './Users';
function App() {
  return (
    <Router>
      <div style={{ display: 'flex', minHeight: '100vh' }}>
        {}
        <nav style={{
          width: '250px',
          backgroundColor: '#2c3e50',
          color: 'white',
          padding: '20px 0',
          boxShadow: '2px 0 10px rgba(0,0,0,0.1)',
          position: 'sticky',
          top: 0,
          height: '100vh'
        }}>
          <div style={{
            padding: '0 20px',
            marginBottom: '30px',
            fontSize: '24px',
            fontWeight: 'bold',
            display: 'flex',
            alignItems: 'center'
          }}>
            <span style={{ color: '#3498db', marginRight: '10px' }}>‚ò∞</span>
            <span>Dashboard</span>
          </div>

          <ul style={{ listStyle: 'none', padding: 0 }}>
            {[
              { to: '/posts', icon: 'üìù', text: 'Posts' },
              { to: '/albums', icon: 'üì∑', text: 'Albums' },
              { to: '/todos', icon: '‚úÖ', text: 'Todos' },
              { to: '/users', icon: 'üë•', text: 'Users' },
              { to: '/comments', icon: 'üí¨', text: 'Comments' }
            ].map((item, index) => (
              <li key={index} style={{ marginBottom: '5px' }}>
                <Link 
                  to={item.to}
                  style={{
                    display: 'flex',
                    margin: '0',
                    alignItems: 'center',
                    padding: '12px 20px',
                    color: 'white',
                    textDecoration: 'none',
                    borderRadius: '4px',
                    transition: 'all 0.3s ease',
                    fontSize: '16px',
                    ':hover': {
                      backgroundColor: '#34495e'
                    }
                  }}
                  activeStyle={{
                    backgroundColor: '#3498db',
                    fontWeight: 'bold'
                  }}
                >
                  <span style={{ marginRight: '15px', fontSize: '18px' }}>{item.icon}</span>
                  {item.text}
                </Link>
              </li>
            ))}
          </ul>

          <div style={{
            marginTop: 'auto',
            padding: '20px',
            fontSize: '12px',
            color: '#7f8c8d',
            textAlign: 'center',
            borderTop: '1px solid #34495e'
          }}>
            ¬© 2025 made by Glebasik and Mishanya
          </div>
        </nav>

        <main style={{ 
          flex: 1, 
          padding: '30px',
          backgroundColor: '#f5f7fa'
        }}>
          <Routes>
            <Route path="/posts" element={<Posts />} />
            <Route path="/albums" element={<Albums />} />
            <Route path="/todos" element={<TODOS />} />
            <Route path="/users" element={<Users />} />
            <Route path="/comments" element={<Comments />} />
            <Route path="/" element={<Posts />} />
          </Routes>
        </main>
      </div>
    </Router>
  );
}

export default App;
