import React, { useState } from "react";

const DataSet = ({ columns, data, renderRow, renderHeader }) => {
  const [selectedRows, setSelectedRows] = useState(new Set());

  // Определяем заголовки, если они не переданы
  const headers = columns || (data.length > 0 ? Object.keys(data[0]).map(key => ({ key, label: key })) : []);

  const handleRowClick = (id, event) => {
    setSelectedRows((prev) => {
      let newSelection = new Set(prev);
      if (event.ctrlKey) {
        newSelection.has(id) ? newSelection.delete(id) : newSelection.add(id);
      } else {
        newSelection = new Set(newSelection.has(id) ? [] : [id]);
      }
      return newSelection;
    });
  };

  return (
    <div className="overflow-x-auto">
      <table className="w-full border-collapse border border-gray-300">
        <thead>
          <tr className="bg-gray-100">
            <th className="border px-4 py-2 w-12"></th> {/* Область выделения */}
            {headers.map((col, index) => (
              <th key={index} className="border px-4 py-2 font-bold text-left">{renderHeader ? renderHeader(col) : col.label}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {data.map((row, index) => (
            <tr
              key={index}
              className={`border cursor-pointer transition-colors ${selectedRows.has(index) ? "bg-blue-200" : "hover:bg-gray-100"}`}
              onClick={(e) => handleRowClick(index, e)}
            >
              <td className="border px-4 py-2 bg-gray-200"></td>
              {headers.map((col, colIndex) => (
                <td key={colIndex} className="border px-4 py-2">
                  {renderRow ? renderRow(row, col) : row[col.key]}
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default DataSet;
