import React, { useState, useEffect } from 'react';
import axios from 'axios';
import CitySelector from './CitySelector';
import WeatherDisplay from './WeatherDisplay';
import './App.css';

const API_KEY = 'eb4825f6f3d9d0ae861db8f355075906';

function App() {
  const [weatherData, setWeatherData] = useState(null);
  const [city, setCity] = useState('Novosibirsk');

  useEffect(() => {
    const fetchWeather = async () => {
      try {
        const geoResponse = await axios.get(`http://api.openweathermap.org/geo/1.0/direct?q=${city}&limit=1&appid=${API_KEY}`);
        const { lat, lon } = geoResponse.data[0];
        const weatherResponse = await axios.get(`https://api.openweathermap.org/data/2.5/forecast?lat=${lat}&lon=${lon}&appid=${API_KEY}&units=metric`);
        setWeatherData(weatherResponse.data);
      } catch (error) {
        console.error('Error fetching weather data', error);
      }
    };

    fetchWeather();
    const interval = setInterval(fetchWeather, 3 * 60 * 60 * 1000); // Обновление каждые 3 часа
    return () => clearInterval(interval);
  }, [city]);

  return (
    <div className="App">
      <CitySelector setCity={setCity} />
      {weatherData && <WeatherDisplay weatherData={weatherData} city={city} />}
    </div>
  );
}

export default App;
